import React from 'react';
/* global XLS */
import './App.css';

class App extends React.Component{
    state = {
      gaz: '',
      koef: 1,
      sum: 0,
    }
    handleChangeGaz = (e)=>{
      this.setState({gaz: e.currentTarget.value})
    }
    handleChangeKoef = (e)=>{
      this.setState({koef: e.currentTarget.value})
    }
    filePicked
    filePicked =(oEvent)=> {
      var self = this;
        // Get The File From The Input
    var oFile = oEvent.target.files[0];

    // Create A File Reader HTML5
    var reader = new FileReader();

    // Ready The Event For When A File Gets Selected
    reader.onload = function(e) {
        var data = e.target.result;
        var cfb = XLS.CFB.read(data, {type: 'binary'});
        var wb = XLS.parse_xlscfb(cfb);
        // Loop Over Each Sheet
        wb.SheetNames.forEach(function(sheetName) {             
            var oJS = XLS.utils.sheet_to_row_object_array(wb.Sheets[sheetName]);   
            // console.log(oJS[1].total) //вывести сумму из 2го объекта
            var sum_all_arr = oJS.map(function(sum){
              return sum.total
            });           
              // console.log(sum_all_arr);//массив из сумм
             var SUM = function(array){
                var sum = 0;
                for(var i = 0; i < array.length; i++){
                  sum += parseInt ("array[i]", 10);//сложить все суммы, перевод из сток в числа
                }                
                console.log(sum);// выводит итоговую сумму за всех клиентов 

              }
              SUM(sum_all_arr);
        });
    };
    // Tell JS To Start Reading The File.. You could delay this if desired
    reader.readAsBinaryString(oFile);
    }

    render(){
      // const Stavka = 16000;
      // const Dobavlenie_procent = 520;
      // let Total = ((...+y)-(x+z))*j

      const {gaz, koef} = this.state
      return(
        <form className = 'form_zp'>
          <input 
          id="upload" 
          type = "file"  
          name="files[]" 
          className = 'input_file' 
          onChange = {this.filePicked}
          />
          <input 
          type = 'text'
          className = 'input_gazolin'
          onChange = {this.handleChangeGaz}
          placeholder = 'бензин сумма'
          value = {gaz}
          />
          <input 
          type = 'text'
          className = 'input_koef'
          onChange = {this.handleChangeKoef}
          placeholder = 'коэффициент'
          value = {koef}
          />
          <p className = 'Total_zp'>Итого:{}</p>
        </form>
      )
    }
  }

export default App;
