import React from 'react';
/* global XLS */
import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
class App extends React.Component{
    state = {
      gaz: 0,
      koef: 1,
      sum: 0,
      klienty_plus: 0,
      klienty_minus: 0,
      zapravka: 0,
      car: 0,
    }
    handleChangeGaz = (e)=>{
      this.setState({gaz: e.currentTarget.value})
    }
    handleChangeKoef = (e)=>{
      this.setState({koef: e.currentTarget.value})
    }
    handleChangeKlientyPlus = (e)=>{
      this.setState({klienty_plus: e.currentTarget.value})
    }
    handleChangeKlientyMinus = (e)=>{
      this.setState({klienty_minus: e.currentTarget.value})
    }
    handleChangeZapravka = (e)=>{
      this.setState({zapravka: e.currentTarget.value})
    }
    handleChangeCar = (e)=>{
      this.setState({car: e.currentTarget.value})
    }
    filePicked =(oEvent)=> {
      var self = this;
        // Get The File From The Input
    var oFile = oEvent.target.files[0];

    // Create A File Reader HTML5
    var reader = new FileReader();

    // Ready The Event For When A File Gets Selected
    reader.onload = function(e) {
        var data = e.target.result;
        var cfb = XLS.CFB.read(data, {type: 'binary'});
        var wb = XLS.parse_xlscfb(cfb);
        // Loop Over Each Sheet
        wb.SheetNames.forEach(function(sheetName) {             
            var oJS = XLS.utils.sheet_to_row_object_array(wb.Sheets[sheetName]);   
            // console.log(oJS[1].total) //вывести сумму из 2го объекта
            var sum_all_arr = oJS.map(function(sum){
              return sum.total
            });           
              // console.log(sum_all_arr);//массив из сумм
             var SUM = function(array){
                var sum = 0;
                for(var i = 0; i < array.length; i++){
                  sum += parseInt (array[i], 10);//сложить все суммы, перевод из сток в числа
                }                
                // console.log(sum);// выводит итоговую сумму за всех клиентов 
                self.setState({sum})  //обновляем состояние суммы    
              }
              SUM(sum_all_arr);
        });
    };
    // Tell JS To Start Reading The File.. You could delay this if desired
    reader.readAsBinaryString(oFile);
    }

    render(){
      const Stavka = 15480
      const {gaz, koef, sum, klienty_minus, klienty_plus, zapravka, car} = this.state
      const First = sum * koef
      const Second = klienty_plus - klienty_minus
      const Third = (First + Second) 
      const Fourth = car * 0.5
      const Fife = Third + parseInt(zapravka, 10)
      return(
        <div className = 'section'>
        <div className = 'container'>
        <div className = 'row'>
        <div className = 'col-lg-6 col-lg-offset-3'>
        <form className = ''>
          <div className = 'form-group'>
            <label for = 'upload'>Выберите файл</label>
            <input 
              id="upload" 
              type = "file"  
              name="files[]" 
              className = 'form-control-file' 
              placeholder = 'выберите файл xls'
              onChange = {this.filePicked}
            />
            {/* <input 
              type = 'text'
              // className = 'input_gazolin'
              onChange = {this.handleChangeGaz}
              placeholder = 'бензин сумма'
              value = {gaz}
            /> */}
            <label for = 'vvod_koef'>Введите коэффициент</label>
            <input 
              type = 'text'
              id = 'vvod_koef'
              className = 'form-control'
              onChange = {this.handleChangeKoef}
              placeholder = 'коэффициент'
              value = {koef}
            />
            <label for = 'za_kl_ing'>+ Клиенты инженера</label>
            <input 
              type = 'text'
              id = 'za_kl_ing'
              className = 'form-control'
              onChange = {this.handleChangeKlientyPlus}
              placeholder = 'плюс клиенты инженера'
              value = {klienty_plus}
            />
            <label for = 'min_kl_ing'>- Клиенты инженера</label>
            <input 
              type = 'text'
              id = 'min_kl_ing'
              className = 'form-control'
              onChange = {this.handleChangeKlientyMinus}
              placeholder = 'минус клиенты инженера'
              value = {klienty_minus}
            />
            <label for = 'zapravka'>Заправка картриджей</label>
            <input 
              type = 'text'
              id = 'zapravka'
              className = 'form-control'
              onChange = {this.handleChangeZapravka}
              placeholder = 'заправка'
              value = {zapravka}
            />
            <label for = 'car'>Расходы на машину</label>
            <input 
              type = 'text'
              id = 'car'
              className = 'form-control'
              onChange = {this.handleChangeCar}
              placeholder = 'расходы на машину'
              value = {car}
            />
          <p>Итого:{Fife - Fourth - Stavka}</p>
        </div>
        </form>
        </div>
        </div>
        </div>
        </div>
      )
    }
  }

export default App;
